class PrintHelper(object):
    '''
    classdocs
    '''
    NL = '\n'

    def __init__(self, logpath):
        '''
        Constructor
        '''
        self.logf = open(logpath,'w')
        print('there')
        
    def printLog(self, strdata):
        self.logf.write(strdata)
        self.logf.flush()
        
    def printLogDict(self, **kwargs):
        self.logf.write(', '.join(['{}={!r}'.format(k, v) for k, v in kwargs.items()]))
        self.logf.flush()
        
        
        
        
        
        
        
        
    